package top.jacktu.page.bean;import java.io.Serializable;public class Result implements Serializable {	private static final long serialVersionUID = -6632439828687866058L;	private Object data;	private String msg;	private int total;	private boolean success = true;	public Result() {	}	public Result(Object data, String msg, int total, boolean success) {		this.data = data;		this.msg = msg;		this.total = total;		this.success = success;	}	public Object getData() {		return this.data;	}	public void setData(Object data) {		this.data = data;	}	public String getMessage() {		return this.msg;	}	public void setMessage(String message) {		this.msg = message;	}	public int getTotal() {		return this.total;	}	public void setTotal(int total) {		this.total = total;	}	public boolean isSuccess() {		return this.success;	}	public void setSuccess(boolean success) {		this.success = success;	}}